List&lt;T&gt;ArrayList <pre><code>/* 定义一个方法用来输出 */
private static void Print(string log, List&lt;string&gt; list)
{
    Console.Write(log + ":");
    list.ForEach(x =&gt; Console.Write(x));
    Console.WriteLine();
}
private static void Main(string[] args)
{
    // 实例化
    List&lt;string&gt; list = new List&lt;string&gt;();
    // 添加
    list.Add("A");
    list.Add("B");
    list.Add("C");
    list.Add("D");
    list.Add("E");
    list.Add("F");
    list.Add("G");
    // 属性
    Console.WriteLine(list.Capacity);
    Console.WriteLine(list.Count);
    // 遍历
    Console.Write("遍历：");
    foreach (String s in list)
        Console.Write(s);
    Console.WriteLine();
    // 索引
    Console.WriteLine("索引：" + list[5]);
    // 移除
    list.Remove("G");
    Print("根据元素移除", list);
    list.RemoveAt(0);
    Print("根据索引移除", list);
    // 反转
    list.Reverse();
    Print("反转", list);
    // 清空
    list.Clear();
    Print("清空", list);
}
// Output:
// 8
// 7
// 遍历：ABCDEFG
// 索引：F
// 根据元素移除:ABCDEF
// 根据索引移除:BCDEF
// 反转:FEDCB
// 清空:
